    private void BtnOkPerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnOkPerActionPerformed

        try {
            Persona p = new Persona();
            String TIdeP = JCBTipoIdePer.getSelectedItem().toString();
            String IdeP = txtIdePer.getText();
            String NombreP = txtNombrePer.getText();
            String ApellidoP = txtApellidoPer.getText();
            String Carg = JCBoxCargoPer.getSelectedItem().toString();

            p.setAll( TIdeP,IdeP, NombreP,ApellidoP,Carg,IdeP);
            PDao.create(p);
            JOptionPane.showMessageDialog(null, "Bienvenido a Redytel: "+NombreP+" "+ ApellidoP+" ☺.");
            txtIde.setText(IdeP);
            txtBienvenido.setText("Bienvenido a Redytel: "+NombreP+" "+ ApellidoP+".");
            JPaneVenGas.setVisible(true);
            Jpanelregistrarse.setVisible(false);
            JpanelIngresar.setVisible(false);
        } catch (SQLException exguardarV) {
            JOptionPane.showMessageDialog(this, "Usuario Ya Rgistrado, Verificar Su Identificacion");
        }
    }//GEN-LAST:event_BtnOkPerActionPerformed

