    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            String usu=txtUsuario.getText();
            String pass=txtContraseña.getText();
            String Darwin="1061751297";
            String Mauricio="1088973525";
            Persona p=PDao.getObject(usu);

            if(p.getIdentificacion().equals(Mauricio)||p.getIdentificacion().equals(Darwin)){
                JOptionPane.showMessageDialog(null, "Bienvenido a Redytel: "+p.getNombre()+" "+ p.getApellido()+" ☺.");
                txtIde.setText(usu);
                txtBienvenido.setText("Bienvenido a Redytel: "+p.getNombre()+" "+ p.getApellido()+".");
                JpanelIngresar.setVisible(false);
                JPaneVenGas.setVisible(true);

            }else if(p.getIdentificacion().equals(usu)&&p.getCargo().equals(pass)){
                JOptionPane.showMessageDialog(null, "Bienvenido a Redytel: "+p.getNombre()+" "+ p.getApellido()+" ☺.");
                txtIde.setText(usu);
                txtBienvenido.setText("Bienvenido a Redytel: "+p.getNombre()+" "+ p.getApellido()+".");
                JPaneVenGas.setVisible(true);
                JpanelIngresar.setVisible(false);

            }else{
                JOptionPane.showMessageDialog(null, " Revisar los Datos\n Contraseña Incorrecta o Usuario Invalido\n De Lo Contrario Dar Clic En Registrarse!");
            }
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, " Revisar los Datos\n Contraseña Incorrecta o Usuario Invalido\n De Lo Contrario Dar Clic En Registrarse!");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

